class NarrativeManager {
    constructor() {
        this.flags = new Set(); // Story flags (e.g., "met_r2")
    }

    setFlag(flag) {
        this.flags.add(flag);
    }

    hasFlag(flag) {
        return this.flags.has(flag);
    }

    triggerEvent(eventId) {
        console.log(`NarrativeManager: Triggering event ${eventId}`);
